'use strict';

const fs = require('fs');

let json = null
class getconfig  {
	constructor(args) {
		if(json == null){
			json = require("../../resources/config.json");
		}
	}
	getVariable(){
		return json['variable'];
	}

	getDev(){
		return json['dev'];
	}
	
	getPro(){
		return json['pro'];
	}

}

module.exports = getconfig;